import React, { useState, useEffect } from "react";
import axios from "axios";
import Board from "./components/Board";
import Feed from "./components/Feed";
import CreateTaskForm from "./components/CreateTaskForm";
import SettingsPage from "./components/SettingsPage";
import LogsPage from "./components/LogsPage";
import Login from "./components/Login";
import "./styles.css";

function App() {
  const [tasks, setTasks] = useState([]);
  const [stories, setStories] = useState([]);
  const [user, setUser] = useState(null);

  const fetchTasks = async () => {
    if (!user) return;
    try {
      const res = await axios.get("http://localhost:8000/tasks/");
      setTasks(res.data);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  const fetchStories = async () => {
    if (!user) return;
    try {
      const res = await axios.get(`http://localhost:8000/stories?viewer_username=${user.username}`);
      setStories(res.data);
    } catch (error) {
      console.error("Error fetching stories:", error);
    }
  };

  useEffect(() => {
    if (user) {
      fetchTasks();
      fetchStories();
    }
  }, [user]);

  if (!user) {
    return <Login setUser={setUser} />;
  }

  return (
    <div className="App">
      <header>
        <h1>Gamified Task Portal</h1>
        <p>Welcome, {user.username} ({user.character_name})</p>
      </header>
      <main>
        <CreateTaskForm refreshTasks={fetchTasks} currentUser={user} />
        <Board tasks={tasks} refreshTasks={fetchTasks} user={user} />
        <Feed stories={stories} />
        <SettingsPage currentUser={user} refreshUser={() => {
          axios.get(`http://localhost:8000/users/${user.username}`)
            .then(res => setUser(res.data))
            .catch(err => console.error(err));
        }} />
        <LogsPage />
      </main>
    </div>
  );
}

export default App;
