import React, { useState } from "react";
import axios from "axios";
import CONFIG from "../config";
import { logFrontendEvent } from "../utils/logger";

function SettingsPage({ currentUser, refreshUser }) {
  const [confirmDone, setConfirmDone] = useState(currentUser.confirm_done);
  const [confirmStartAdventure, setConfirmStartAdventure] = useState(currentUser.confirm_start_adventure);

  const handleToggleDone = async () => {
    try {
      const newSetting = !confirmDone;
      await axios.put(`${CONFIG.BACKEND_URL}/users/${currentUser.username}/settings`, {
        confirm_done: newSetting
      });
      setConfirmDone(newSetting);
      refreshUser();
      logFrontendEvent(`User ${currentUser.username} changed confirm_done to ${newSetting}`);
    } catch (error) {
      console.error("Error updating settings:", error);
    }
  };

  const handleToggleStart = async () => {
    try {
      const newSetting = !confirmStartAdventure;
      await axios.put(`${CONFIG.BACKEND_URL}/users/${currentUser.username}/settings`, {
        confirm_start_adventure: newSetting
      });
      setConfirmStartAdventure(newSetting);
      refreshUser();
      logFrontendEvent(`User ${currentUser.username} changed confirm_start_adventure to ${newSetting}`);
    } catch (error) {
      console.error("Error updating settings:", error);
    }
  };

  return (
    <div className="settings">
      <h2>Settings</h2>
      <label>
        <input type="checkbox" checked={confirmDone} onChange={handleToggleDone} />
        Always Confirm “Done”?
      </label>
      <br />
      <label>
        <input type="checkbox" checked={confirmStartAdventure} onChange={handleToggleStart} />
        Confirm start adventure? (Once you start, you cannot go back!)
      </label>
    </div>
  );
}

export default SettingsPage;
