import React, { useState } from "react";
import axios from "axios";
import CONFIG from "../config";
import { logFrontendEvent } from "../utils/logger";

function CreateTaskForm({ refreshTasks, currentUser }) {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [color, setColor] = useState("blue");
  const [isPrivate, setIsPrivate] = useState(false);
  const [coOwners, setCoOwners] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const taskData = {
        title,
        description,
        color,
        is_private: isPrivate,
        owner_username: currentUser.username,
        co_owners: coOwners // comma-separated list of usernames
      };
      await axios.post(`${CONFIG.BACKEND_URL}/tasks`, taskData);
      setTitle("");
      setDescription("");
      setColor("blue");
      setIsPrivate(false);
      setCoOwners("");
      refreshTasks();
      logFrontendEvent(`Task created: ${title} by ${currentUser.username}`);
    } catch (error) {
      console.error("Error creating task:", error);
    }
  };

  return (
    <div className="create-task-form">
      <h2>Create a New Task</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Title" value={title} onChange={(e) => setTitle(e.target.value)} required />
        <input type="text" placeholder="Description (optional)" value={description} onChange={(e) => setDescription(e.target.value)} />
        <select value={color} onChange={(e) => setColor(e.target.value)}>
          <option value="blue">Blue</option>
          <option value="green">Green</option>
          <option value="red">Red</option>
          <option value="yellow">Yellow</option>
        </select>
        <label>
          <input type="checkbox" checked={isPrivate} onChange={(e) => setIsPrivate(e.target.checked)} />
          Private Task
        </label>
        <input type="text" placeholder="Co-owners (comma separated usernames)" value={coOwners} onChange={(e) => setCoOwners(e.target.value)} />
        <button type="submit">Add Task</button>
      </form>
    </div>
  );
}

export default CreateTaskForm;
