// frontend/src/components/Card.js
import React from 'react';
import axios from 'axios';

function Card({ task, refreshTasks }) {
  // Function to change task status via API call
  const handleStatusChange = async (newStatus) => {
    try {
      await axios.put(`http://localhost:8000/tasks/${task.id}/status`, { new_status: newStatus });
      refreshTasks();
    } catch (error) {
      console.error("Error updating task status:", error);
    }
  };

  return (
    <div className={`card ${task.color}`}>
      <h3>{task.title}</h3>
      <p>{task.description}</p>
      <div className="card-actions">
        <button onClick={() => handleStatusChange("Doing")}>Doing</button>
        <button onClick={() => handleStatusChange("Waiting")}>Waiting</button>
        <button onClick={() => handleStatusChange("Done")}>Done</button>
      </div>
    </div>
  );
}

export default Card;
