// frontend/src/App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Board from './components/Board';
import Feed from './components/Feed';
import CreateTaskForm from './components/CreateTaskForm';
import './styles.css';

function App() {
  const [tasks, setTasks] = useState([]);
  const [stories, setStories] = useState([]);

  useEffect(() => {
    fetchTasks();
    fetchStories();
  }, []);

  const fetchTasks = async () => {
    try {
      const res = await axios.get('http://localhost:8000/tasks/');
      setTasks(res.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const fetchStories = async () => {
    try {
      const res = await axios.get('http://localhost:8000/stories/');
      setStories(res.data);
    } catch (error) {
      console.error('Error fetching stories:', error);
    }
  };

  return (
    <div className="App">
      <header>
        <h1>Gamified Task Portal</h1>
      </header>
      <main>
        {/* Our new form component */}
        <CreateTaskForm refreshTasks={fetchTasks} />

        {/* The Trello-like board */}
        <Board tasks={tasks} refreshTasks={fetchTasks} />

        {/* The story feed */}
        <Feed stories={stories} />
      </main>
    </div>
  );
}

export default App;
