import React, { useState } from "react";
import axios from "axios";

function SettingsPage({ currentUser, refreshUser }) {
  const [confirmDone, setConfirmDone] = useState(currentUser.confirm_done);

  const handleToggle = async () => {
    try {
      const newSetting = !confirmDone;
      await axios.put(`http://localhost:8000/users/${currentUser.username}/settings`, {
        confirm_done: newSetting
      });
      setConfirmDone(newSetting);
      refreshUser();
    } catch (error) {
      console.error("Error updating settings:", error);
    }
  };

  return (
    <div style={{ marginTop: "20px" }}>
      <h2>Settings</h2>
      <label>
        <input type="checkbox" checked={confirmDone} onChange={handleToggle} />
        Always Confirm “Done”?
      </label>
    </div>
  );
}

export default SettingsPage;
