import React, { useState } from 'react';
import axios from 'axios';

function CreateTaskForm({ refreshTasks }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [color, setColor] = useState('blue');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Adjust this payload if your backend expects different keys
      const taskData = {
        title,
        description,
        color
      };

      await axios.post('http://localhost:8000/tasks/', taskData);
      // Clear form fields
      setTitle('');
      setDescription('');
      setColor('blue');
      // Refresh the task list in the parent component
      refreshTasks();
    } catch (error) {
      console.error('Error creating task:', error);
    }
  };

  return (
    <div style={{ marginBottom: '20px' }}>
      <h2>Create a New Task</h2>
      <form onSubmit={handleSubmit} style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Description (optional)"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <select value={color} onChange={(e) => setColor(e.target.value)}>
          <option value="blue">Blue</option>
          <option value="green">Green</option>
          <option value="red">Red</option>
          <option value="yellow">Yellow</option>
          {/* Add more label colors as desired */}
        </select>
        <button type="submit">Add Task</button>
      </form>
    </div>
  );
}

export default CreateTaskForm;
